mapper.select:
SELECT max(create_time) create_time,game_type FROM uic_burying_point
WHERE game_type IS NOT NULL AND game_type !=3
AND user_id = #{userId} AND burying_type = 7
AND channel_id = #{channelId}
AND create_time >= #{begin} AND create_time <=#{end}
GROUP BY game_type
ORDER BY create_time DESC LIMIT #{gameNum}

select create_time from uic_burying_point
where burying_type=#{buryingType} and user_id=#{userId}
order by create_time DESC limit 0,1

SELECT user_id userId, game_type gameType, channel_id channelId, count(1) pointCount, DATE_FORMAT(create_time,"%H") buryingHour, DATE_FORMAT(create_time,"%Y-%m-%d") buryingDate FROM `uic_burying_point` WHERE 1=1 AND delete_flag = 0
AND create_time >= #{beginDate}
AND create_time <= #{endDate}
AND user_id !=0 AND burying_type=8 GROUP BY user_id,game_type,channel_id,DATE_FORMAT(create_time,"%Y-%m-%d"),DATE_FORMAT(create_time,"%H") ORDER BY DATE_FORMAT(create_time,"%Y-%m-%d"),DATE_FORMAT(create_time,"%H") LIMIT 10000000
