mapper.select:
SELECT id userId, nickname, third_id thirdId, reg_channel_id channelId, create_time registeredTime, DATE_FORMAT(create_time,"%H") registeredHour, DATE_FORMAT(create_time,"%Y-%m-%d") registeredDate FROM `uic_user` WHERE 1=1
AND create_time >= #{beginDate}
AND create_time <= #{endDate}
AND delete_flag = 0 LIMIT 10000000;

SELECT from_base64(t.third_id) thirdId
FROM uic_user t
WHERE 1=1

    AND t.user_source <= #{userSource}


    AND t.create_time >= #{beginDate}


    AND t.create_time <= #{endDate}

ORDER BY id ASC LIMIT #{start}, #{length}

SELECT COUNT(*) FROM uic_user t
WHERE 1=1

    AND t.user_source = #{userSource}


    AND t.create_time >= #{beginDate}


    AND t.create_time <= #{endDate}
