mapper.select:
SELECT betting_date searchDate, COUNT(DISTINCT user_id) userCount, SUM(betting_amount) bettingAmount, SUM(betting_count) bettingCount, SUM(result_amount) resultAmount FROM `dataware_betting_log_day` WHERE 1=1
AND game_type= #{gameType}
AND channel_id = #{channelId}
AND betting_date = #{searchDate}
AND channel_id LIKE CONCAT(#{parentId}, '%')
AND user_group=2 AND delete_flag=0
