SELECT IFNULL(COUNT(DISTINCT user_id),0)
FROM `dataware_burying_point_day`
WHERE 1=1

    AND game_type= #{gameType}


    AND channel_id = #{channelId}


    AND burying_date = #{searchDate}


    AND parent_id = #{parentId}

AND user_group=2
AND delete_flag=0

