mapper.select:
SELECT DISTINCT user_id
FROM dataware_burying_point_hour
WHERE 1=1 AND delete_flag = 0

    AND burying_date = #{buryingDate}


    AND user_group = #{userGroup}

SELECT user_id userId, game_type gameType, channel_id channelId, sum(point_count) pointCount, user_group userGroup, burying_date buryingDate FROM dataware_burying_point_hour WHERE 1=1 AND delete_flag = 0
AND burying_date = #{buryingDate}
AND user_group = #{userGroup}
GROUP BY user_id,channel_id,game_type,burying_date

SELECT COUNT(*) FROM dataware_burying_point_hour t
WHERE  1=1 AND t.delete_flag = 0

    AND  t.burying_date = #{buryingDate}


    AND  t.burying_hour = #{buryingHour}
