mapper.select:
SELECT user_id userId, channel_id channelId, biz_type bizType, SUM(recharge_count) rechargeCount, SUM(third_amount) thirdAmount, convert_date convertDate FROM `dataware_convert_hour` WHERE 1=1 AND delete_flag = 0 AND channel_id IS NOT NULL
AND convert_date = #{convertDate}
GROUP BY user_id,channel_id,biz_type,convert_date

SELECT COUNT(*) FROM dataware_convert_hour t
WHERE  1=1 AND t.delete_flag = 0
AND t.channel_id IS NOT NULL

    AND  t.convert_date = #{convertDate}


    AND  t.convert_hour = #{convertHour}


    AND t.convert_hour >= #{startHour}


    AND t.convert_hour <= #{endHour}

SELECT
COUNT(DISTINCT t.user_id) rechargeCount,
SUM(t.third_amount) rechargeAmount
FROM `dataware_convert_hour` t
WHERE 1=1 AND t.delete_flag = 0
AND t.channel_id IS NOT NULL

    AND t.convert_hour = #{convertHour}


    AND t.convert_date = #{convertDate}


    AND t.channel_id = #{channelId}


    AND t.parent_id = #{parentId}

SELECT
COUNT(DISTINCT t.user_id) rechargeCount,
SUM(t.third_amount) rechargeAmount
FROM `dataware_convert_hour` t
WHERE 1=1 AND t.delete_flag = 0
AND t.channel_id IS NOT NULL

    AND t.convert_hour <= #{convertHour}


    AND t.convert_date = #{convertDate}


    AND t.channel_id = #{channelId}


    AND t.parent_id = #{parentId}

mapper.delete:
delete FROM
dataware_convert_hour
WHERE 1=1

    AND  convert_date = #{convertDate}


    AND  convert_hour = #{convertHour}


    AND convert_hour >= #{startHour}


    AND convert_hour <= #{endHour}
