mapper.select:
SELECT COUNT(*) FROM dataware_final_channel_info_hour t
WHERE 1=1
AND t.delete_flag =0

    AND t.business_hour = #{businessHour}


    AND t.business_date = #{businessDate}


    AND t.channel_id = #{channelId}


    AND t.parent_id = #{parentId}

SELECT   FROM dataware_final_channel_info_hour t
WHERE 1=1
AND t.delete_flag =0

    AND t.business_hour = #{businessHour}


    AND t.business_date = #{businessDate}


    AND t.channel_id = #{channelId}


    AND t.parent_id = #{parentId}

order by t.business_hour asc

SELECT business_hour businessHour, SUM(hour_dau) hourDau, SUM(hour_user_count) hourUserCount, SUM(hour_recharge_count) hourRechargeCount, SUM(hour_recharge_amount) hourRechargeAmount, SUM(hour_new_users) hourNewUsers, SUM(hour_betting_amount) hourBettingAmount, SUM(hour_diff_amount) hourDiffAmount FROM `dataware_final_channel_info_hour` WHERE 1=1
AND channel_id = #{channelId}
AND parent_id = #{parentId}
AND business_date >= #{beginDate}
AND business_date <= #{endDate}
GROUP BY business_hour ORDER BY business_hour ASC
