SELECT betting_date searchDate, IFNULL(COUNT(DISTINCT user_id),0) userCount, IFNULL(SUM(betting_amount),0) bettingAmount, IFNULL(SUM(betting_count),0) bettingCount, IFNULL(SUM(result_amount),0) resultAmount FROM `dataware_betting_log_day` WHERE 1=1 AND channel_id IS NOT NULL
AND game_type= #{gameType}
AND channel_id = #{channelId}
AND betting_date = #{searchDate}
AND parent_id =#{parentId}
AND user_group=2 AND delete_flag=0

