SELECT  FROM dataware_final_channel_info_all t
WHERE 1=1 AND t.delete_flag =0

    AND t.channel_id = #{channelId}


    AND t.parent_id = #{parentId}


    AND t.business_date >= #{beginDate}


    AND t.business_date <= #{endDate}

order by t.business_date asc

SELECT  FROM dataware_final_channel_info_all t
WHERE 1=1 AND t.delete_flag =0

    AND t.channel_id = #{channelId}


    AND t.parent_id = #{parentId}


    AND t.business_date = #{date}

SELECT DATE_FORMAT(business_date,'%Y-%m') `month`, IFNULL(SUM(recharge_amount),0) sumRecharge, IFNULL(SUM(dau),0) sumDau, IFNULL(SUM(new_users),0) sumNewUsers, IFNULL(COUNT(id),0) days FROM `dataware_final_channel_info_all` WHERE 1=1
AND DATE_FORMAT(business_date,'%Y-%m') >= #{beginMonth}
AND DATE_FORMAT(business_date,'%Y-%m') <= #{endMonth}
AND channel_id = #{channelId}
AND parent_id = #{parentId}
GROUP BY DATE_FORMAT(business_date,'%Y-%m') ORDER BY DATE_FORMAT(business_date,'%Y-%m') ASC

mapper.delete:
delete
FROM dataware_final_channel_info_all
WHERE 1=1
AND delete_flag =0

    AND business_date = #{businessDate}


    AND channel_id = #{channelId}


    AND parent_id = #{parentId}

mapper.update:
UPDATE dataware_final_channel_info_all SET
his_registered = #{hisRegistered},
his_recharge = #{hisRecharge},
user_ltv = #{userLtv},
WHERE 1=1
AND delete_flag =0

    AND business_date = #{businessDate}


    AND channel_id = #{channelId}


    AND parent_id = #{parentId}

