mapper.select:
SELECT FROM dataware_final_entrance_analysis t WHERE 1=1 AND t.delete_flag = 0
AND t.business_date >= #{beginDate}
AND t.business_date <= #{endDate}
order by t.business_date

SELECT
    SUM(t.entrance_dau)
FROM
    dataware_final_entrance_analysis t
WHERE
    t.delete_flag = 0

        AND t.business_date = #{beginDate}


        AND t.active_user_type = #{activeUserType}


        AND t.convert_user_type = #{convertUserType}

SELECT
    t.entrance_dau
FROM
    dataware_final_entrance_analysis t
WHERE
    t.delete_flag = 0

    AND t.business_date = #{beginDate}


    AND t.active_user_type = #{activeUserType}


    AND t.convert_user_type = #{convertUserType}


    AND t.event_id = #{eventId}
