mapper.delete:
delete FROM
dataware_user_info
WHERE 1=1

    AND registered_date = #{registeredDate}


    AND registered_hour = #{registeredHour}


    AND registered_hour >= #{startHour}


    AND registered_hour <= #{endHour}

