mapper.select:
SELECT id, user_id, parent_id, channel_id, registered_time, user_group, registered_date FROM dataware_user_info WHERE 1=1 AND delete_flag = 0
AND registered_date <= #{endDate}
ORDER BY user_id ASC LIMIT #{minIndex},#{maxIndex}
