mapper.select:
SELECT DATE_FORMAT(create_time, '%Y-%m-%d') searchDate, IFNULL(COUNT(DISTINCT user_id),0) userCount, IFNULL(SUM(amount),0) bettingAmount, IFNULL(SUM(return_amount),0) resultAmount FROM `landlords_user_amount_log` WHERE 1=1
AND create_time >= #{beginDate}
AND create_time <= #{endDate}
AND channel_id LIKE CONCAT(#{parentId}, '%')
AND status=1 AND delete_flag=0 GROUP BY DATE_FORMAT(create_time, '%Y-%m-%d');

SELECT IFNULL(SUM(t.amount),0) tableAmount FROM `landlords_user_amount_log` t WHERE 1=1
AND t.create_time >= #{beginDate}
AND t.create_time <= #{endDate}
AND t.channel_id LIKE CONCAT(#{parentId}, '%')
AND t.type =1 AND t.status=1 AND t.delete_flag=0

SELECT IFNULL(COUNT(user_id),0) gameTimes FROM `landlords_user_amount_log` t WHERE 1=1
AND t.create_time >= #{beginDate}
AND t.create_time <= #{endDate}
AND t.channel_id LIKE CONCAT(#{parentId}, '%')
AND t.type IN (2,3) AND t.status=1 AND t.delete_flag=0

SELECT aa.searchDate, aa.desk_type, aa.userCount, aa.bettingAmount, aa.resultAmount, bb.tableAmount, cc.gameTimes FROM ( SELECT DATE_FORMAT(create_time, '%Y-%m-%d') searchDate, desk_type, IFNULL(COUNT(DISTINCT user_id), 0) userCount, IFNULL(SUM(amount), 0) bettingAmount, IFNULL(SUM(return_amount), 0) resultAmount FROM `landlords_user_amount_log` WHERE 1 = 1
AND create_time >= #{beginDate}
AND create_time <= #{endDate}
AND channel_id LIKE CONCAT(#{parentId}, '%')
AND desk_type in
#{item}
AND STATUS = 1 AND delete_flag = 0 GROUP BY desk_type, DATE_FORMAT(create_time, '%Y-%m-%d') ) aa LEFT JOIN ( SELECT DATE_FORMAT(create_time, '%Y-%m-%d') searchDate, desk_type, IFNULL(SUM(t.amount), 0) tableAmount FROM `landlords_user_amount_log` t WHERE 1 = 1
AND t.create_time >= #{beginDate}
AND t.create_time <= #{endDate}
AND t.channel_id LIKE CONCAT(#{parentId}, '%')
AND t.desk_type in
#{item}
AND t.type = 1 AND t. STATUS = 1 AND t.delete_flag = 0 GROUP BY DATE_FORMAT(create_time, '%Y-%m-%d'), desk_type ) bb ON aa.searchDate = bb.searchDate AND aa.desk_type = bb.desk_type LEFT JOIN ( SELECT DATE_FORMAT(create_time, '%Y-%m-%d') searchDate, desk_type, IFNULL(COUNT(user_id), 0) gameTimes FROM `landlords_user_amount_log` t WHERE 1 = 1
AND t.create_time >= #{beginDate}
AND t.create_time <= #{endDate}
AND t.channel_id LIKE CONCAT(#{parentId}, '%')
AND t.desk_type in
#{item}
AND t.type IN (2, 3) AND t. STATUS = 1 AND t.delete_flag = 0 GROUP BY DATE_FORMAT(create_time, '%Y-%m-%d'), desk_type ) cc ON aa.searchDate = cc.searchDate AND aa.desk_type = cc.desk_type;
