SELECT SUM(IFNULL(t2.rmb_amount,0)) FROM `inventory_phy_awards_sendlog` t1 LEFT JOIN inventory_phy_awards_info t2 ON t1.phy_awards_id=t2.id WHERE 1=1 AND t1.delete_flag=0 AND t1.send_time BETWEEN #{beginDate} AND #{endDate} AND t1.receive_status=3
AND t1.activity_id in
#{item}

SELECT t1.activity_type AS activityType, SUM(IFNULL(t2.rmb_amount,0)) AS rmbAmount FROM `inventory_phy_awards_sendlog` t1 LEFT JOIN inventory_phy_awards_info t2 ON t1.phy_awards_id=t2.id WHERE 1=1 AND t1.delete_flag=0 AND t1.send_time BETWEEN #{beginDate} AND #{endDate} AND t1.receive_status=3
AND t1.activity_type = #{activityType}
AND t1.channel_id = #{parentId}
GROUP BY t1.activity_type

SELECT COUNT(DISTINCT(user_id))
FROM inventory_phy_awards_sendlog t
WHERE  t.delete_flag=0
AND t.group_id =10

    AND t.channel_id= #{parentId}


    AND t.activity_type= #{activityType}


    AND t.send_time >= #{beginDate}


    AND t.send_time <= #{endDate}

SELECT COUNT(DISTINCT(user_id)) as userCount,
  t.activity_type as activityType
FROM inventory_phy_awards_sendlog t
WHERE  t.delete_flag=0
AND t.group_id =10

    AND t.channel_id= #{parentId}


    AND t.send_time >= #{beginDate}


    AND t.send_time <= #{endDate}

GROUP BY t.activity_type

SELECT t1.channel_id AS parentId, SUM(IFNULL(t2.rmb_amount,0)) AS rmbAmount FROM `inventory_phy_awards_sendlog` t1 LEFT JOIN inventory_phy_awards_info t2 ON t1.phy_awards_id=t2.id WHERE 1=1 AND t1.delete_flag=0 AND t1.send_time BETWEEN #{beginDate} AND #{endDate} AND t1.receive_status=3
AND t1.activity_type = #{activityType}
GROUP BY t1.channel_id

SELECT COUNT(DISTINCT(user_id)) as userCount,
t.channel_id as parentId
FROM inventory_phy_awards_sendlog t
WHERE  t.delete_flag=0
AND t.group_id =10

    AND t.activity_type= #{activityType}


    AND t.send_time >= #{beginDate}


    AND t.send_time <= #{endDate}

GROUP BY t.channel_id
