mapper.select:
SELECT user_id userId, channel_id channelId, COUNT(CASE WHEN betting_type!=5 THEN 1 END) bettingCount, SUM(amount) bettingAmount, SUM(return_amount) resultAmount, DATE_FORMAT(create_time,"%Y-%m-%d") bettingDate, DATE_FORMAT(create_time,"%H") bettingHour FROM `tcard_user_betting_log` WHERE 1=1
AND channel_id = #{channelId}
AND `status` != -1 AND create_time BETWEEN #{beginDate} AND #{endDate} GROUP BY user_id,channel_id,DATE_FORMAT(create_time,"%Y-%m-%d"),DATE_FORMAT(create_time,"%H") ORDER BY DATE_FORMAT(create_time,"%Y-%m-%d"),DATE_FORMAT(create_time,"%H")

SELECT SUM(amount) bettingAmount, SUM(return_amount) resultAmount FROM `tcard_user_betting_log` WHERE 1=1 AND `status` != -1
AND channel_id LIKE CONCAT( #{parentId}, '%')
AND channel_id = #{channelId}
AND user_id not in
#{item}
AND create_time BETWEEN #{beginDate} AND #{endDate} GROUP BY DATE_FORMAT(create_time,"%Y-%m-%d")
