mapper.select:
SELECT	ip
 FROM `uic_user_log`
 WHERE 1=1
 AND create_time  BETWEEN #{beginDate} AND #{endDate}
 GROUP BY ip HAVING count(DISTINCT user_id)>= #{ipCount}

SELECT user_id, ip FROM uic_user_log WHERE 1=1 AND create_time BETWEEN #{beginDate} AND #{endDate}
AND ip IN
#{item}
GROUP BY ip,user_id
